function gen_otp(mobile, res, successCallback, errorCallback) {
    var otp = Math.floor(Math.random() * (999999 - 111111 + 1)) + 111111;
    var where = {};
    where["otp"] = otp;
    Users.findOne(where).then((response) => {
        if (response == null) {
            var where = {};
            where["mobile"] = mobile;
            where["deleted"] = 0;
            Users.findOneAndUpdate(where, { otp: otp }, { new: true })
                .exec()
                .then((response) => {
                    axios
                        .get("https://2factor.in/API/V1/d4ab2eaa-d61e-11ea-9fa5-0200cd936042/SMS/" + mobile + "/" + otp + "/SMSOTP", {})
                        .then(function (response) {
                            ////console.log(response.data);
                            successCallback(otp);
                        })
                        .catch(function (error) {
                            res.status(200).send({
                                status: "error",
                                message: error.message,
                            });
                        });
                })
                .catch((error) => {
                    gen_otp(mobile, res);
                });
        } else {
            gen_otp(mobile, res);
        }
    });
}

async function email_otp(email) {
    var otp = Math.floor(Math.random() * (999999 - 111111 + 1)) + 111111;
    var where = {};
    where["email_otp"] = otp;
    Users.findOne(where).then((response) => {
        if (response == null) {
            var where = {};
            where["email"] = email;
            Users.findOneAndUpdate(
                where,
                {
                    email_otp: otp,
                },
                {
                    new: true,
                }
            )
                .exec()
                .then((response) => {
                    let transporter = nodemailer.createTransport({
                        host: SMTP_HOST,
                        port: SMTP_PORT,
                        //secure: process.env.SMTP_SECURE, // true for 465, false for other ports
                        auth: {
                            user: SMTP_USERNAME, // generated ethereal user
                            pass: SMTP_PASSWORD, // generated ethereal password
                        },
                    });

                    transporter
                        .sendMail({
                            from: EMAIL_FROM, // sender address
                            to: email, // list of receivers
                            subject: "Your OTP is: " + otp, // Subject line
                            html: "Hi,<br/> Your OTP is: " + otp + " <br/> Thanks.  ", // html body
                        })
                        .catch((error) => {
                            console.log(error);
                        });
                })
                .catch((error) => {
                    email_otp(email);
                });
        } else {
            email_otp(email);
        }
    });
}

async function send_email(to, subject, message) {
    let transporter = nodemailer.createTransport({
        host: SMTP_HOST,
        port: SMTP_PORT,
        //secure: process.env.SMTP_SECURE, // true for 465, false for other ports
        auth: {
            user: SMTP_USERNAME, // generated ethereal user
            pass: SMTP_PASSWORD, // generated ethereal password
        },
    });

    transporter
        .sendMail({
            from: EMAIL_FROM, // sender address
            to: to, // list of receivers
            subject: subject, // Subject line
            html: message, // html body
        })
        .then((success) => {
            //console.log(success);
        })
        .catch((error) => {
            console.log(error);
        });
}

