    state_list_all: function (req, res) {
        const errors = validationResult(req);
        if (Object.keys(errors.array()).length > 0) {
            res.status(200).send({
                status: "validation_error",
                errors: errors.array(),
                token: req.token,
            });
        } else {
            var where = {};

            if (req.query.name != "") {
                where["name"] = {
                    $regex: ".*" + req.query.name,
                    $options: "i",
                };
            }

            if (req.query.active != "") {
                where["active"] = req.query.active;
            }

            where["deleted"] = 0;

            States.find(where, null, {
                limit: parseInt(req.query.limit),
                skip: parseInt(req.query.page),
            })
                .sort({
                    created_date: -1,
                })
                .then((response) => {
                    States.find(where).countDocuments(function (err, count) {
                        res.status(200).send({
                            status: "success",
                            token: req.token,
                            result: response,
                            totalCount: count,
                        });
                    });
                })
                .catch((error) => {
                    res.status(200).send({
                        status: "error",
                        message: error,
                        token: req.token,
                    });
                });
        }
    },

    update_state_status: function (req, res) {
        const errors = validationResult(req);
        if (Object.keys(errors.array()).length > 0) {
            res.status(200).send({
                status: "validation_error",
                errors: errors.array(),
                token: req.token,
            });
        } else {
            var where = {};
            where["_id"] = req.body.id;
            States.findOne(where)
                .then((response) => {
                    if (response == null) {
                        res.status(200).send({
                            status: "error",
                            message: "States not found",
                            token: req.token,
                        });
                    } else {
                        if (response.active == 0) {
                            var active = 1;
                        } else {
                            var active = 0;
                        }
                        States.findOneAndUpdate(
                            where,
                            {
                                active: active,
                            },
                            {
                                new: true,
                            }
                        )
                            .exec()
                            .then((response) => {
                                res.status(200).send({
                                    status: "success",
                                    message: "Status updated",
                                    token: req.token,
                                    active: active,
                                });
                            })
                            .catch((error) => {
                                res.status(200).send({
                                    status: "error",
                                    message: "Something went wrong",
                                    token: req.token,
                                });
                            });
                    }
                })
                .catch((error) => {
                    res.status(200).send({
                        status: "error",
                        message: "Something went wrong",
                        token: req.token,
                    });
                });
        }
    },

