    check_stock: function (req, res) {
        const errors = validationResult(req);
        if (Object.keys(errors.array()).length > 0) {
            res.status(200).send({
                status: "validation_error",
                errors: errors.array(),
                token: req.token,
            });
        } else {
            var where = {};
            if (req.query.provider != "office") {
                where["vendor"] = req.query.vendor;
                where["product"] = req.query.product;
                Rate.findOne(where)
                    .populate("product")
                    .populate("vendor")
                    .then((response) => {
                        res.status(200).send({
                            status: "success",
                            token: req.token,
                            result: response,
                            stock: null,
                        });
                    })
                    .catch((error) => {
                        res.status(200).send({
                            status: "error",
                            message: "Invalid product id",
                            token: req.token,
                        });
                    });
            } else {
                where["vendor"] = req.query.vendor;
                where["product"] = req.query.product;
                Rate.findOne(where)
                    .populate("product")
                    .populate("vendor")
                    .then((response) => {
                        var where = {};
                        where["inward_office"] = mongoose.Types.ObjectId(req.query.office);
                        where["type"] = 0;
                        where["product"] = mongoose.Types.ObjectId(req.query.product);
                        StockTransactionProduct.aggregate([
                            {
                                $match: where,
                            },
                            {
                                $group: {
                                    _id: "_id",
                                    totalValue: {
                                        $sum: "$qty",
                                    },
                                },
                            },
                        ]).then((sum_inward) => {
                            var inward = 0;
                            if (sum_inward[0]) {
                                var inward = sum_inward[0].totalValue;
                            }

                            var where = {};
                            where["office"] = mongoose.Types.ObjectId(req.query.office);
                            where["type"] = 1;
                            where["product"] = mongoose.Types.ObjectId(req.query.product);
                            StockTransactionProduct.aggregate([
                                {
                                    $match: where,
                                },
                                {
                                    $group: {
                                        _id: "_id",
                                        totalValue: {
                                            $sum: "$qty",
                                        },
                                    },
                                },
                            ]).then((sum_outward) => {
                                var outward = 0;
                                if (sum_outward[0]) {
                                    var outward = sum_outward[0].totalValue;
                                }

                                res.status(200).send({
                                    status: "success",
                                    token: req.token,
                                    result: response,
                                    stock: Number(inward) - Number(outward),
                                });
                            });
                        });
                    });
            }
        }
    },

