    update_landing_extra_sort: async function (req, res) {
        var orders = req.body.orders;

        for (let i = 0; i < orders.length; i++) {
            var where = {};
            where["_id"] = orders[i].id;
            await Landing.findOneAndUpdate(
                where,
                { sort: orders[i].order },
                {
                    new: true,
                }
            ).exec();
        }

        res.status(200).send({
            status: "success",
            message: "Data has been updated",
            token: req.token,
        });
    },

